import { useState } from 'react';

function Form({ add }) {
    const [titleInput, title] = useState('')
    const [descInput, desc] = useState('')

    const handleOnChange = (e) => {
        setUserInput(e.currentTarget.value)
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        const note = {
            title: titleInput,
            desc: descInput
        }
        add(note)
        setUserInput("")
        // console.log("subnited")
    }

    const hadleKeyPress = (e) => {
        if (e.key === "Enter") {
            handleSubmit(e)
        }
    }

    return (
        <form onSubmit={handleSubmit}>
            <div class="addNote">
                <h4>New note</h4>

                <label for="">Note title</label>
                <input
                    value={titleInput}
                    type="text"
                    onChange={handleOnChange}
                    onKeyDown={hadleKeyPress}
                />

                <label for="">Description</label>
                <textarea
                    value={descInput}
                    cols="30" rows="10"
                    onChange={handleOnChange}
                    onKeyDown={hadleKeyPress}></textarea>

                <button>
                    {/* <img src="/media/addBtn.png" alt="" /> */}submit
                </button>
            </div>
        </form>
    )
}

export default Form;
