import { useState } from 'react';


//modifies frontend https://codepen.io/kalpeshsingh/pen/wMNpLp

function App() {

  // const operations = ["+", "-", "*", "/"];
  // const [calc, setCalc] = useState("");
  // const [res, setRes] = useState("");

  const createDigits = () => {
    const digits = [];

    for (let i = 3; i > 0; i--) {
      digits.push(
        <div class="calc-button-row">

          <div class="button" key={i * 3} value={i * 3} onClick={this.digitTap()}>{i * 3}</div>
          <div class="button" key={i * 3 - 1} value={i * 3 - 1} onClick={this.digitTap()}>{i * 3 - 1}</div>
          <div class="button" key={i * 3 - 2} value={i * 3 - 2} onClick={this.digitTap()}>{i * 3 - 2}</div>

        </div>
      )
    }
    return digits;
  }

  return (
    <div className="App">
      <div class="container">
      <div class="calc-body">

        <div class="calc-screen">
          <div class="calc-operation"> </div>
          <div class="calc-typed">{calc || 0}</div>
        </div>

        <div class="calc-button-row">
          <div class="button l" value="C" >+</div>
          <div class="button l" value="not eq"  >-</div>
          <div class="button l" value="%^"  >x</div>
          <div class="button l" value="/"  >/</div>
          <div class="button c" value="C" >C</div>
        </div>

        {createDigits()}

        <div class="calc-button-row">
          <div class="button" value="." onClick="this.tapBtn(this.value)">.</div>
          <div class="button" value="0" onClick="this.tapBtn(this.value)">0</div>
          <div class="button" value="<" onClick="this.tapBtn(this.value)"> = </div>
        </div>
      </div>
    </div>
    </div>

  );

}

export default App;
