import { useState } from 'react';

function App() {
  function digitTap() { }

  const createDigits = () => {
    const digits = [];

    for (let i=3;i>0;i--) {

      digits.push(
        <div class="calc-button-row">

            <div class="button" key={i*3} value={i*3} onClick={this.digitTap()}>{i*3}</div>
            <div class="button" key={i*3-1} value={i*3-1} onClick={this.digitTap()}>{i*3-1}</div>
            <div class="button" key={i*3-2} value={i*3-2} onClick={this.digitTap()}>{i*3-2}</div>

        </div>
      )
    }
    return digits;
  };

  return (
    <div class="container">
      <div class="calc-body">

        <div class="calc-screen">
          <div class="calc-operation"> </div>
          <div ref={this.output} class="calc-typed"><span class="blink-me">_</span></div>
        </div>

        <div class="calc-button-row">
          <div class="button l" value="C" onClick="this.tapBtn(this.value)">+</div>
          <div class="button l" value="not eq" onClick="this.tapBtn(this.value)">-</div>
          <div class="button l" value="%^" onClick="this.tapBtn(this.value)">*</div>
          <div class="button l" value="/" onClick="this.tapBtn(this.value)">/</div>
          <div class="button c" value="C" onClick="this.tapBtn(this.value)">C</div>
        </div>

        {createDigits()}
        {/* <div class="calc-button-row">
          <div class="button" value="7" onClick="this.tapBtn(this.value)">7</div>
          <div class="button" value="8" onClick="this.tapBtn(this.value)">8</div>
          <div class="button" value="9" onClick="this.tapBtn(this.value)">9</div> 
        </div>
        <div class="calc-button-row">
          <div class="button" value="4" onClick="this.tapBtn(this.value)">4</div>
          <div class="button" value="5" onClick="this.tapBtn(this.value)">5</div>
          <div class="button" value="6" onClick="this.tapBtn(this.value)">6</div> 
        </div>
        <div class="calc-button-row">
          <div class="button" value="1" onClick="this.tapBtn(this.value)">1</div>
          <div class="button" value="2" onClick="this.tapBtn(this.value)">2</div>
          <div class="button" value="3" onClick="this.tapBtn(this.value)">3</div> 
        </div> */}

        <div class="calc-button-row">
          <div class="button" value="." onClick="this.tapBtn(this.value)">.</div>
          <div class="button" value="0" onClick="this.tapBtn(this.value)">0</div>
          <div class="button" value="<" onClick="this.tapBtn(this.value)"> = </div>
        </div>
      </div>
    </div>
  );

}

export default App;
